import{_ as a,c as l,o as i,aj as r}from"./chunks/framework.CFr_S-Bc.js";const m=JSON.parse('{"title":"基础组件 API 参考","description":"Torch-RecHub 基础组件详细 API 文档，包括特征处理和数据转换","frontmatter":{"title":"基础组件 API 参考","description":"Torch-RecHub 基础组件详细 API 文档，包括特征处理和数据转换"},"headers":[],"relativePath":"cache/api-basic.md","filePath":"cache/api-basic.md"}'),o={name:"cache/api-basic.md"};function t(d,e,n,c,s,h){return i(),l("div",null,[...e[0]||(e[0]=[r('<h1 id="基础组件-api-参考" tabindex="-1">基础组件 API 参考 <a class="header-anchor" href="#基础组件-api-参考" aria-label="Permalink to “基础组件 API 参考”">​</a></h1><p>本文档详细介绍 Torch-RecHub 中的基础组件，包括特征处理、数据转换等基础功能。</p><h2 id="特征处理" tabindex="-1">特征处理 <a class="header-anchor" href="#特征处理" aria-label="Permalink to “特征处理”">​</a></h2><h3 id="特征列-feature-columns" tabindex="-1">特征列 (Feature Columns) <a class="header-anchor" href="#特征列-feature-columns" aria-label="Permalink to “特征列 (Feature Columns)”">​</a></h3><h4 id="densefeature" tabindex="-1">DenseFeature <a class="header-anchor" href="#densefeature" aria-label="Permalink to “DenseFeature”">​</a></h4><ul><li><strong>简介</strong>：处理连续型数值特征。</li><li><strong>参数</strong>： <ul><li><code>name</code> (str): 特征名称</li><li><code>dimension</code> (int): 特征维度</li><li><code>dtype</code> (str): 数据类型，默认&#39;float32&#39;</li></ul></li></ul><h4 id="sparsefeature" tabindex="-1">SparseFeature <a class="header-anchor" href="#sparsefeature" aria-label="Permalink to “SparseFeature”">​</a></h4><ul><li><strong>简介</strong>：处理离散型类别特征。</li><li><strong>参数</strong>： <ul><li><code>name</code> (str): 特征名称</li><li><code>vocabulary_size</code> (int): 类别词表大小</li><li><code>embedding_dim</code> (int): 嵌入向量维度</li><li><code>dtype</code> (str): 数据类型，默认&#39;int32&#39;</li><li><code>embedding_name</code> (str): 嵌入层名称，默认None</li></ul></li></ul><h4 id="varlensparsefeature" tabindex="-1">VarLenSparseFeature <a class="header-anchor" href="#varlensparsefeature" aria-label="Permalink to “VarLenSparseFeature”">​</a></h4><ul><li><strong>简介</strong>：处理变长离散型特征。</li><li><strong>参数</strong>： <ul><li><code>name</code> (str): 特征名称</li><li><code>vocabulary_size</code> (int): 类别词表大小</li><li><code>embedding_dim</code> (int): 嵌入向量维度</li><li><code>maxlen</code> (int): 序列最大长度</li><li><code>dtype</code> (str): 数据类型，默认&#39;int32&#39;</li><li><code>embedding_name</code> (str): 嵌入层名称，默认None</li><li><code>combiner</code> (str): 序列池化方式，可选&#39;sum&#39;，&#39;mean&#39;，&#39;max&#39;，默认&#39;mean&#39;</li></ul></li></ul><h2 id="数据转换" tabindex="-1">数据转换 <a class="header-anchor" href="#数据转换" aria-label="Permalink to “数据转换”">​</a></h2><h3 id="数据预处理" tabindex="-1">数据预处理 <a class="header-anchor" href="#数据预处理" aria-label="Permalink to “数据预处理”">​</a></h3><h4 id="minmaxscaler" tabindex="-1">MinMaxScaler <a class="header-anchor" href="#minmaxscaler" aria-label="Permalink to “MinMaxScaler”">​</a></h4><ul><li><strong>简介</strong>：数值特征归一化。</li><li><strong>参数</strong>： <ul><li><code>feature_range</code> (tuple): 归一化范围，默认(0, 1)</li></ul></li></ul><h4 id="standardscaler" tabindex="-1">StandardScaler <a class="header-anchor" href="#standardscaler" aria-label="Permalink to “StandardScaler”">​</a></h4><ul><li><strong>简介</strong>：数值特征标准化。</li><li><strong>参数</strong>： <ul><li><code>with_mean</code> (bool): 是否移除均值，默认True</li><li><code>with_std</code> (bool): 是否按标准差缩放，默认True</li></ul></li></ul><h4 id="labelencoder" tabindex="-1">LabelEncoder <a class="header-anchor" href="#labelencoder" aria-label="Permalink to “LabelEncoder”">​</a></h4><ul><li><strong>简介</strong>：类别特征编码。</li><li><strong>方法</strong>： <ul><li><code>fit(values)</code>: 拟合编码器</li><li><code>transform(values)</code>: 转换数据</li><li><code>fit_transform(values)</code>: 拟合并转换</li></ul></li></ul><h3 id="数据格式转换" tabindex="-1">数据格式转换 <a class="header-anchor" href="#数据格式转换" aria-label="Permalink to “数据格式转换”">​</a></h3><h4 id="pandas-to-torch" tabindex="-1">pandas_to_torch <a class="header-anchor" href="#pandas-to-torch" aria-label="Permalink to “pandas_to_torch”">​</a></h4><ul><li><strong>简介</strong>：将Pandas数据转换为PyTorch张量。</li><li><strong>参数</strong>： <ul><li><code>df</code> (pd.DataFrame): 输入DataFrame</li><li><code>dense_cols</code> (list): 连续特征列名列表</li><li><code>sparse_cols</code> (list): 离散特征列名列表</li><li><code>device</code> (str): 设备类型，&#39;cpu&#39;或&#39;cuda&#39;</li></ul></li></ul><h4 id="numpy-to-torch" tabindex="-1">numpy_to_torch <a class="header-anchor" href="#numpy-to-torch" aria-label="Permalink to “numpy_to_torch”">​</a></h4><ul><li><strong>简介</strong>：将NumPy数组转换为PyTorch张量。</li><li><strong>参数</strong>： <ul><li><code>arrays</code> (list): NumPy数组列表</li><li><code>device</code> (str): 设备类型，&#39;cpu&#39;或&#39;cuda&#39;</li></ul></li></ul><h2 id="模型组件" tabindex="-1">模型组件 <a class="header-anchor" href="#模型组件" aria-label="Permalink to “模型组件”">​</a></h2><h3 id="激活函数" tabindex="-1">激活函数 <a class="header-anchor" href="#激活函数" aria-label="Permalink to “激活函数”">​</a></h3><h4 id="dice" tabindex="-1">Dice <a class="header-anchor" href="#dice" aria-label="Permalink to “Dice”">​</a></h4><ul><li><strong>简介</strong>：Dice激活函数，在深度兴趣网络(DIN)中提出。</li><li><strong>参数</strong>： <ul><li><code>epsilon</code> (float): 平滑参数，默认1e-3</li><li><code>device</code> (str): 设备类型，默认&#39;cpu&#39;</li></ul></li></ul><h3 id="注意力机制" tabindex="-1">注意力机制 <a class="header-anchor" href="#注意力机制" aria-label="Permalink to “注意力机制”">​</a></h3><h4 id="scaleddotproductattention" tabindex="-1">ScaledDotProductAttention <a class="header-anchor" href="#scaleddotproductattention" aria-label="Permalink to “ScaledDotProductAttention”">​</a></h4><ul><li><strong>简介</strong>：缩放点积注意力机制。</li><li><strong>参数</strong>： <ul><li><code>temperature</code> (float): 缩放温度参数</li><li><code>attn_dropout</code> (float): 注意力Dropout比率</li></ul></li></ul><h4 id="multiheadattention" tabindex="-1">MultiHeadAttention <a class="header-anchor" href="#multiheadattention" aria-label="Permalink to “MultiHeadAttention”">​</a></h4><ul><li><strong>简介</strong>：多头注意力机制。</li><li><strong>参数</strong>： <ul><li><code>d_model</code> (int): 模型维度</li><li><code>n_heads</code> (int): 注意力头数</li><li><code>d_k</code> (int): 键向量维度</li><li><code>d_v</code> (int): 值向量维度</li><li><code>dropout</code> (float): Dropout比率</li></ul></li></ul>',32)])])}const b=a(o,[["render",t]]);export{m as __pageData,b as default};
