import{_ as a,c as r,o,aj as t}from"./chunks/framework.BuEeO6_n.js";const u=JSON.parse('{"title":"Core Components Overview","description":"Torch-RecHub core components overview","frontmatter":{"title":"Core Components Overview","description":"Torch-RecHub core components overview"},"headers":[],"relativePath":"core/intro.md","filePath":"en/core/intro.md"}'),n={name:"core/intro.md"};function i(l,e,s,c,d,h){return o(),r("div",null,[...e[0]||(e[0]=[t('<h1 id="core-components-overview" tabindex="-1">Core Components Overview <a class="header-anchor" href="#core-components-overview" aria-label="Permalink to “Core Components Overview”">​</a></h1><p>Torch-RecHub is modular: features, data, models, training, and tools are separated for clarity and extensibility.</p><h2 id="architecture" tabindex="-1">Architecture <a class="header-anchor" href="#architecture" aria-label="Permalink to “Architecture”">​</a></h2><ol><li><strong>Feature layer</strong> – dense, sparse, and sequence feature definitions.</li><li><strong>Data layer</strong> – loading, preprocessing, and dataloader generation.</li><li><strong>Model layer</strong> – ranking, matching, multi-task, and generative models.</li><li><strong>Training layer</strong> – unified trainers for fit/eval/predict/ONNX export.</li><li><strong>Tools layer</strong> – ONNX export, visualization, callbacks, losses, etc.</li></ol><h2 id="component-relations" tabindex="-1">Component Relations <a class="header-anchor" href="#component-relations" aria-label="Permalink to “Component Relations”">​</a></h2><ul><li>Feature layer guides preprocessing in the data layer.</li><li>Data generators feed the training layer.</li><li>Models are consumed by trainers.</li><li>Trainers call tools for export/visualization/tracking.</li></ul><h2 id="component-details" tabindex="-1">Component Details <a class="header-anchor" href="#component-details" aria-label="Permalink to “Component Details”">​</a></h2><ul><li><strong>Feature processing</strong>: <code>DenseFeature</code>, <code>SparseFeature</code>, <code>SequenceFeature</code>. See <a href="/torch-rechub/en/core/features.html">Features</a>.</li><li><strong>Data pipeline</strong>: <code>TorchDataset</code>, <code>PredictDataset</code>, <code>DataGenerator</code>, <code>MatchDataGenerator</code>. See <a href="/torch-rechub/en/core/data.html">Data</a>.</li><li><strong>Training &amp; evaluation</strong>: <code>CTRTrainer</code>, <code>MatchTrainer</code>, <code>MTLTrainer</code> (and generative trainer variants). See <a href="/torch-rechub/en/core/evaluation.html">Training &amp; Evaluation</a>.</li></ul>',8)])])}const m=a(n,[["render",i]]);export{u as __pageData,m as default};
