import{_ as i,c as l,o as e,ah as r,ai as t,aj as n}from"./chunks/framework.B-q_H0pu.js";const g=JSON.parse('{"title":"项目介绍","description":"Torch-RecHub 项目架构、特性和设计原理概览","frontmatter":{"title":"项目介绍","description":"Torch-RecHub 项目架构、特性和设计原理概览"},"headers":[],"relativePath":"zh/introduction.md","filePath":"zh/introduction.md"}'),o={name:"zh/introduction.md"};function s(d,a,h,c,b,m){return e(),l("div",null,[...a[0]||(a[0]=[r('<h1 id="项目介绍" tabindex="-1">项目介绍 <a class="header-anchor" href="#项目介绍" aria-label="Permalink to “项目介绍”">​</a></h1><h2 id="项目概述" tabindex="-1">项目概述 <a class="header-anchor" href="#项目概述" aria-label="Permalink to “项目概述”">​</a></h2><p><strong>Torch-RecHub</strong> 是一个使用 PyTorch 构建的、灵活且易于扩展的推荐系统框架。它旨在简化推荐算法的研究和应用，提供常见的模型实现、数据处理工具和评估指标。</p><p><img src="'+t+'" alt="Torch-RecHub 横幅" title="Torch-RecHub 横幅"></p><h2 id="特性" tabindex="-1">特性 <a class="header-anchor" href="#特性" aria-label="Permalink to “特性”">​</a></h2><ul><li><strong>模块化设计:</strong> 易于添加新的模型、数据集和评估指标。</li><li><strong>基于 PyTorch:</strong> 利用 PyTorch 的动态图和 GPU 加速能力。</li><li><strong>丰富的模型库:</strong> 包含多种经典和前沿的推荐算法（请在下方列出）。</li><li><strong>标准化流程:</strong> 提供统一的数据加载、训练和评估流程。</li><li><strong>易于配置:</strong> 通过配置文件或命令行参数轻松调整实验设置。</li><li><strong>可复现性:</strong> 旨在确保实验结果的可复现性。</li><li><strong>易扩展:</strong> 模型训练与模型定义解耦，无basemodel概念。</li><li><strong>原生函数:</strong> 尽可能使用pytorch原生的类与函数，不做过多定制。</li><li><strong>模型代码精简:</strong> 在符合论文思想的基础上方便新手学习</li><li><strong>其他特性:</strong> 例如，支持负采样、多任务学习等。</li></ul><h2 id="整体架构" tabindex="-1">整体架构 <a class="header-anchor" href="#整体架构" aria-label="Permalink to “整体架构”">​</a></h2><p><img src="'+n+'" alt="架构设计图" title="架构设计图"></p><h2 id="数据层设计" tabindex="-1">数据层设计 <a class="header-anchor" href="#数据层设计" aria-label="Permalink to “数据层设计”">​</a></h2><h3 id="特征类" tabindex="-1">特征类 <a class="header-anchor" href="#特征类" aria-label="Permalink to “特征类”">​</a></h3><p>数值型特征</p><ul><li>例如年龄、薪资、日点击量等</li></ul><p>类别型特征</p><ul><li>例如城市、学历、性别等</li><li>LabelEncoder编码，得到Embedding向量</li></ul><p>序列特征</p><ul><li>有序兴趣序列：例如最近一周点击过的item list</li><li>无序标签特征：例如电影类型（动作|悬疑|犯罪）</li><li>LabelEncoder编码，得到序列Embedding向量</li><li>池化，降维</li><li>保留序列，与其他特征进行attention等模型操作</li><li>与Sparse特征共享Embedding Table</li></ul><h3 id="数据类" tabindex="-1">数据类 <a class="header-anchor" href="#数据类" aria-label="Permalink to “数据类”">​</a></h3><ul><li>Dataset</li><li>Dataloader</li></ul><h3 id="工具类" tabindex="-1">工具类 <a class="header-anchor" href="#工具类" aria-label="Permalink to “工具类”">​</a></h3><ul><li>序列特征生成</li><li>样本构造</li><li>负采样</li><li>向量化召回</li></ul><h2 id="模型层设计" tabindex="-1">模型层设计 <a class="header-anchor" href="#模型层设计" aria-label="Permalink to “模型层设计”">​</a></h2><h3 id="模型类" tabindex="-1">模型类 <a class="header-anchor" href="#模型类" aria-label="Permalink to “模型类”">​</a></h3><p>通用Layer 浅层特征建模</p><ul><li>LR：逻辑回归</li><li>MLP：多层感知机，可通过字典设置dims等参数</li><li>EmbeddinLayer：通用Embedding层，含三类特征的处理，维护一个dict格式的EmbeddingTable，输出经过模型所需要的输入embedding</li></ul><p>深层特征建模</p><ul><li>FM、FFM、CIN</li><li>self-attention、target-attention、transformer</li></ul><p>定制Layer</p>',27)])])}const u=i(o,[["render",s]]);export{g as __pageData,u as default};
